import React, { useMemo } from "react";
import Form from "./form/form";
import SimulatorResult from "./simulator-result";
import useFormContext from "../hooks/use-form-context";
import Done from "./icons/done";
import Button from "./button";

export default function Layout() {
  const { submitted } = useFormContext();

  return (
    <div className="flex flex-col lg:grid grid-cols-3 w-full gap-8 ">
      <div className=" flex-2 col-span-2">
        {submitted ? <ThankYou /> : <Form />}
      </div>
      <SimulatorResult />
    </div>
  );
}

const ThankYou = () => {
  const { resetForm } = useFormContext();

  return (
    <div className="flex h-full justify-center items-center">
      <div className="flex space-x-16 p-8">
        <Done />
        <div className="space-y-8">
          <h2 className="font-medium text-4xl text-primary-1">
            Thank you for your submission!
          </h2>
          <p className="font-normal text-lg text-secondary-2">
            We will contact you very soon.
          </p>
          <Button
            onClick={resetForm}
            title="Eine weitere Berechnung vornehmenâ€‹"
          />
        </div>
      </div>
    </div>
  );
};
