import Button from "./components/button";
import EuroIcon from "./components/icons/euro";
import Input from "./components/input";
import StepsInfo from "./components/steps-info";
import { useMemo, useState } from "react";
import { FormProvider } from "./context/form-context";
import Form from "./components/form";
function App() {
  const [step, setStep] = useState(0);
  const numberOfSteps = useMemo(() => 3, []);

  const changeStep = (newStep: number) => {
    if (newStep >= 0 && newStep < numberOfSteps) setStep(newStep);
  };

  return (
    <>
      <FormProvider>
        <Form />
      </FormProvider>
    </>
  );

  return (
    <div className="flex justify-center border-red-500 ">
      <div className="flex-1 p-2 flex flex-col space-y-4">
        <div className="flex justify-between">
          <StepsInfo
            numberOfSteps={numberOfSteps}
            currentStep={step}
            handleStepChange={(newStep) => changeStep(newStep)}
          />
        </div>
      </div>
    </div>
  );
}

export default App;
