import FormInput from "./form-input";
import StepsInfo from "../steps-info";
import useFormContext from "../../hooks/use-form-context";
import Button from "../button";

export default function Form() {
  const { page, handlePageChange, numberOfPages, data, userInfo, canSubmit } =
    useFormContext();

  const submit = () => {
    alert(JSON.stringify({ data, userInfo }));
  };
  return (
    <div className="p-4 space-y-6 flex flex-col">
      <div className="flex justify-between ">
        <FormInput />
      </div>
      <div className="flex justify-end">
        <div className="flex space-x-3 p-1 justify-end">
          {page > 0 && (
            <Button
              title="Back"
              type="secondary"
              onClick={() => {
                handlePageChange(page - 1);
              }}
            ></Button>
          )}
          <Button
            title="Next"
            onClick={() => {
              if (page == numberOfPages - 1 && canSubmit()) submit();
              else handlePageChange(page + 1);
            }}
          ></Button>
        </div>
      </div>
    </div>
  );
}
