interface TextInputProps {
  label?: string;
}
export default function TextInput(props: TextInputProps) {
  const { label = "" } = props;
  return (
    <div className="flex ">
      <label
        htmlFor="helper-text"
        className="block text-sm font-medium text-orange-500"
      >
        {label}
      </label>
      <input type="text" />
    </div>
  );
}
