import {
  createContext,
  useState,
  useEffect,
  ReactNode,
  EventHandler,
  useMemo,
} from "react";

interface IData {
  totalGrossWages: number;
  totalProjectPerMonth: number;
  ordersFromThirdParties: number;
}

interface FormProviderProps {
  children: ReactNode;
}

interface IFormContext {
  data: IData;
  page: number;
  handleChange: (e: any) => void;
  handlePageChange: (value: number) => void;
  numberOfPages: number;
}

const defaultContextValue: IFormContext = {
  page: 0,
  handlePageChange: (value: number) => {},
  handleChange: () => {},
  numberOfPages: 3,

  data: {
    ordersFromThirdParties: 0,
    totalGrossWages: 0,
    totalProjectPerMonth: 0,
  },
};

export const FormContext = createContext<IFormContext>(defaultContextValue);

export const FormProvider = ({ children }: FormProviderProps) => {
  const [page, setPage] = useState(0);
  const [data, setData] = useState<IData>(defaultContextValue.data);
  const handlePageChange = (newPage: number) => {
    if (newPage >= 0 && newPage < defaultContextValue.numberOfPages)
      setPage(newPage);
  };
  const handleChange = (e: any) => {
    const name = e.target.name;

    setData((prevData: IData) => ({
      ...prevData,
      [name]: e.target.value,
    }));
  };

  return (
    <FormContext.Provider
      value={{
        numberOfPages: defaultContextValue.numberOfPages,
        data,
        page,
        handlePageChange,
        handleChange,
      }}
    >
      {children}
    </FormContext.Provider>
  );
};
