import Button from "./components/button";
import EuroIcon from "./components/icons/euro";
import Input from "./components/input";
import StepsInfo from "./components/steps-info";
import { useMemo, useState } from "react";
import { FormProvider } from "./context/form-context";
import Form from "./components/form";
function App() {
  const [step, setStep] = useState(0);
  const numberOfSteps = useMemo(() => 3, []);

  const changeStep = (newStep: number) => {
    if (newStep >= 0 && newStep < numberOfSteps) setStep(newStep);
  };

  return (
    <>
      <FormProvider>
        <Form />
      </FormProvider>
    </>
  );

  return (
    <div className="flex justify-center border-red-500 ">
      <div className="flex-1 p-2 flex flex-col space-y-4">
        <div className="flex justify-between">
          <StepsInfo
            numberOfSteps={numberOfSteps}
            currentStep={step}
            handleStepChange={(newStep) => changeStep(newStep)}
          />
        </div>
        <div className="flex space-x-2">
          <div className="flex flex-col space-y-7">
            <Input
              icon={<EuroIcon />}
              value={675387.76}
              type="number"
              label="Total gross wages of R&D employees/year"
            />
            <Input
              type="number"
              value={135.76}
              action={{
                label: "Monate",
                onClick: () => {},
              }}
              label="Total project months (No limit to the duration of a project)"
            />
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
