import React from "react";
import FormInput from "./form/form-input";
import StepsInfo from "./steps-info";
import useFormContext from "../hooks/use-form-context";
import Button from "./button";

export default function Form() {
  const { page, handlePageChange, numberOfPages } = useFormContext();
  return (
    <div className="p-4 space-y-6">
      <div className="flex justify-between">
        <FormInput />
        <StepsInfo
          numberOfSteps={numberOfPages}
          currentStep={page}
          handleStepChange={(newPage) => handlePageChange(newPage)}
        />
      </div>
      <div className="flex justify-end">
        <div className="flex space-x-3 p-1 md:w-1/2 w-full justify-end">
          <Button
            title="Back"
            type="secondary"
            onClick={() => {
              handlePageChange(page - 1);
            }}
          ></Button>
          <Button
            title="Next"
            onClick={() => {
              handlePageChange(page + 1);
            }}
          ></Button>
        </div>
      </div>
    </div>
  );
}
