import { ReactNode } from "react";
import EuroIcon from "./icons/euro";

interface InputProps {
  label?: string;
  type?: "number" | "text";
  icon?: ReactNode;
  action?: {
    label: string;
    onClick: () => void;
  };
}
export default function Input(props: InputProps) {
  const { label = "", icon: Icon, type = "text", action } = props;
  return (
    <div className="">
      <label className="text-secondary-2">{label} :</label>
      <div className="flex border border-neutrals-900 p-[10px] gap-1 focus-within:border-primary-1">
        {Icon && Icon}
        <input type={type} className="focus:outline-none text-secondary-2" />
        {action && (
          <button
            onClick={action.onClick}
            className="text-primary-1 font-normal"
          >
            {action.label}
          </button>
        )}
      </div>
    </div>
  );
}
