import React from "react";
import useFormContext from "../hooks/use-form-context";
import EuroIcon from "./icons/euro";
import Input from "./input";

export default function FormInput() {
  const { page } = useFormContext();
  const display: Record<number, any> = {
    0: <PayRollForm />,
    1: <InfoForm />,
  };
  return <div className="h-[450px]">{display[page]}</div>;
}

const InfoForm = () => {
  return (
    <div className="space-y-6">
      <div className="space-y-6">
        <h2 className="font-bold text-secondary-2">Eligible Wages</h2>
        <div className="flex flex-col space-y-4">
          <Input label="Title" name="title" />
          <div className="flex space-x-4">
            <Input placeholder="john" label="First name" name="firstName" />
            <Input placeholder="Example" label="Last name" name="lastName" />
          </div>
          <Input placeholder="Example" label="Company" name="company" />
          <div className="flex space-x-4">
            <Input placeholder="+1 000 00 00" label="Phone number" name="phoneNumber" />
            <Input placeholder="example@gmail.com" label="Email address" name="email" />
          </div>
        </div>
      </div>
    </div>
  );
};
const PayRollForm = () => {
  const { handleChange, data } = useFormContext();
  return (
    <div className="space-y-6">
      <div className="space-y-6">
        <h2 className="font-bold text-secondary-2">Eligible Wages</h2>
        <div className="flex flex-col space-y-7">
          <Input
            onChange={handleChange}
            icon={<EuroIcon />}
            type="number"
            value={data.totalGrossWages}
            name="totalGrossWages"
            label="Total gross wages of R&D employees/year"
          />
          <Input
            type="number"
            onChange={handleChange}
            name="totalProjectPerMonth"
            value={data.totalProjectPerMonth}
            action={{
              label: "Monate",
              onClick: () => {},
            }}
            label="Total project months (No limit to the duration of a project)"
          />
        </div>
      </div>

      <div className="space-y-6">
        <h2 className="font-bold text-secondary-2">Contract Research</h2>

        <div className="flex flex-col space-y-7">
          <Input
            onChange={handleChange}
            name="ordersFromThirdParties"
            value={data.ordersFromThirdParties}
            icon={<EuroIcon />}
            type="number"
            label="Orders from third parties for your R&D projects (Net)"
          />
        </div>
      </div>
    </div>
  );
};
