import Button from "./components/button";
import EuroIcon from "./components/icons/euro";
import Input from "./components/input";
import StepsInfo from "./components/steps-info";
import { useMemo, useState } from "react";
function App() {
  const [step, setStep] = useState(0);
  const numberOfSteps = useMemo(() => 3, []);

  const changeStep = (newStep: number) => {
    if (newStep >= 0 && newStep < numberOfSteps) setStep(newStep);
  };

  return (
    <div className="flex justify-center border-red-500">
      <div className="flex-1 p-2">
        <div className="flex justify-between">
          <h2 className="font-bold">Eligible Wages</h2>
          <StepsInfo
            numberOfSteps={numberOfSteps}
            currentStep={step}
            handleStepChange={(newStep) => changeStep(newStep)}
          />
        </div>
        <div className="flex flex-1 border space-x-3 p-1 md:w-1/2 justify-end">
          <Button
            title="Back"
            type="secondary"
            onClick={() => {
              changeStep(step - 1);
            }}
          ></Button>
          <Button
            title="Next"
            onClick={() => {
              changeStep(step + 1);
            }}
          ></Button>
        </div>
      </div>
    </div>
  );
}

export default App;
